$oImageLogo = new d3logo;
$oImageLogo->getLogo();

/**
 * Class d3logo
 */
class d3logo
{
    public function getLogo()
    {
        if ($_GET && isset($_GET['cl']) && $_GET['cl'])
        {
            switch (strtolower($_GET['cl']))
            {
                case 'module_main':
                    $sFncName = '_getEmptyPic';
                    break;
                default:
                    $sFncName = '_getSizeXXS';
            }
        }
        else
        {
            $sFncName = '_getSizeXXS';
        }

        if ($_REQUEST && isset($_REQUEST['size']) && $_REQUEST['size'])
        {
            switch (strtolower($_REQUEST['size']))
            {
                case 'xxs':
                    $sFncName = '_getSizeXXS';
                    break;
                case 'xs':
                    $sFncName = '_getSizeXS';
                    break;
                case 's':
                    $sFncName = '_getSizeS';
                    break;
                case 'm':
                    $sFncName = '_getSizeM';
                    break;
                case 'l':
                    $sFncName = '_getSizeL';
                    break;
                case 'xl':
                    $sFncName = '_getSizeXL';
                    break;
                case 'xxl':
                    $sFncName = '_getSizeXXL';
                    break;
            }
        }

        header("Content-type: image/png");
        echo base64_decode($this->{$sFncName}());
        exit;
    }

